import axios from "axios"
import "./singlepost.css"
import { useLocation } from "react-router"
import { useEffect } from "react"

export default function SinglePost() {
    const location = useLocation()
    const path = location.pathname.split("/")[2];
    const [post, setPost] = useState({})


    useEffect(()=>{
        const getPost = async ()=>{
            const res = await axios.get("/posts/" + path)
            setPost(res.data)
        }
        getPost()
    },[path])
  return (
    <div className="singlePost">
        <div className="singlePostWrapper">
            {post.photo && (
                <img className="singlePostImg" 
                src={post.photo} 
                alt="" />
            )}

            <h1 className="singlePostTitle">{post.title}
                <div className="singlePostEdit">
                    <i className="singlePostIcon far fa-edit"></i>
                    <i className="singlePostIcon far fa-trash-alt"></i>
                </div>
            </h1>
            <div className="singlePostInfo">
                <span className="singlePostAuthor">{post.username} <b>Safak</b></span>
                <span className="singlePostDate">{new Date (post.createdAt).toDateString}</span>
            </div>
            <p className="singlePostDesc">Lorem ipsum dolor sit amet consectetur adipisicing elit. Ratione ipsa sunt est rerum at, cumque vero explicabo placeat vel, eligendi, dignissimos iste voluptatum minus ipsam quae dolore repellendus veritatis quibusdam.
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Non pariatur, perspiciatis consequatur reprehenderit soluta nam a ratione amet praesentium laudantium nesciunt illo consequuntur eum ipsam alias nostrum explicabo. Non, at.
            </p>
        </div>

    </div>
  )
}
